import { Link } from '@material-ui/core';
import { Favorite } from '@material-ui/icons';
import { useEffect, useRef, useState } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import db from './firebase';
import Video from './Video'
import tiktok from './tiktok1.jpg'

function App() {
  const [videos, setVideos] = useState([])
  const [playing, setPlaying] = useState(false)
  const scrollRef = useRef(null);

  useEffect(() => {
    db.collection('videos').onSnapshot(snapshot =>
      setVideos(snapshot.docs.map(doc => doc.data()))
    )

    ReactDOM.findDOMNode(scrollRef.current).addEventListener('scroll', onScrollPauseVideo)
  }, [])

  const onVideoPress = (videoRef) => {
    if (playing) {
      videoRef.target.pause();
      setPlaying(false)
    } else {
      videoRef.target.play();
      setPlaying(true)
    }
  }

  const onScrollPauseVideo = () => {
    setPlaying(false)
  }

  return (
    <div className="app">
      <span className="app__copywrite">
        developed with&nbsp;
        <Favorite fontSize="small" className="app__favoriteIcon" />
        <Link target="_blank" href="https://www.facebook.com/hasibahmadkhaliqi">&nbsp;by hak</Link>
      </span>
      <img className="app__backgroundImage" s src={tiktok} alt="Noooo" />
      <div className="app__videos" ref={scrollRef} onScroll={onScrollPauseVideo}>
        {videos.map(
          (video => (
            <Video
              url={video.url}
              channel={video.channel}
              description={video.description}
              song={video.song}
              likes={video.likes}
              messages={video.messages}
              shares={video.shares}
              onVideoPress={onVideoPress}
              playing={playing}
            />
          ))
        )}
      </div>
    </div>
  );
}

export default App;
