import React, { useRef, useState } from 'react'
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ url, channel, description, song, likes, messages, shares }) {
  const [playing, setPlaying] = useState(false)
  const videoRef = useRef(null);

  const onVideoPress = () => {
    if (playing) {
      videoRef.current.pause();
      setPlaying(false) 
    } else {
      videoRef.current.play();
      setPlaying(true) 
    }
  }

  return (
    <div className="video">
      <video 
        className="video__player"
        src="https://v77.tiktokcdn.com/1c56b908152edd8872028756ede0f1f3/5fe0e772/video/tos/alisg/tos-alisg-pve-0037c001/c96ec4eeedbe4289bdbb50b3b4bd681c/?a=1233&br=614&bt=307&cd=0%7C0%7C1&cr=0&cs=0&cv=1&dr=0&ds=3&er=&l=20201221121935010190218096320AE30E&lr=tiktok_m&mime_type=video_mp4&qs=0&rc=Mzx5OHU3a210eTMzNTczM0ApNjk0Ozk8ODw2Nzc7N2U7NGdlYmAxM29pXmNfLS1hMTRzc18wXjIzYWI2LS9jXi5jXjA6Yw%3D%3D&vl=&vr="
        loop
        onClick={onVideoPress}
        ref={videoRef}
        >
      </video>
      <VideoFooter 
        channel="hasibahmadkh" 
        description="this is a desc.." 
        song="this is the lyrics of song" 
      />

      <VideoSidebar likes={200} messages={83} shares={10} />
    </div>
  )
}

export default Video
