import { Avatar, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Divider, List, ListItem, ListItemAvatar, ListItemText, makeStyles, Slide } from '@material-ui/core';
import { Favorite, FavoriteBorder, Message, Share } from '@material-ui/icons'
import Typography from '@material-ui/core/Typography';
import React, { useEffect, useState } from 'react'
import { db } from './firebase'
import './VideoSidebar.css'

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function VideoSidebar({ likes, messages, shares, videoId, userUid, userName }) {
  const [liked, setLiked] = useState(false)
  const [openMessages, setOpenMessages] = React.useState(false);

  const useStyles = makeStyles((theme) => ({
    paper: { minWidth: "900px" },
    root: {
      width: '100%',
      maxWidth: '36ch',
      backgroundColor: theme.palette.background.paper,
    },
    inline: {
      display: 'inline',
    },
  }));

  const classes = useStyles()

  const handleClickOpenMessages = () => {
    setOpenMessages(true);
  };

  const handleCloseMessages = () => {
    setOpenMessages(false);
  };

  useEffect(() => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes")
      .onSnapshot((snapshot) => {
        snapshot.docs.forEach((doc) => {
          let docUserId = doc.data().userid
          if (docUserId === userUid) {
            setLiked(true)
          } else {
            setLiked(false)
          }
        })
      })

  }, [videoId, userUid])

  const onLikeVideo = () => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes").add({
        "userid": userUid
      }).then(() => {
        setLiked(true)
      })
  }

  const onDisLikeVideo = () => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes").where('userid', '==', userUid)
      .get()
      .then((docs) => {
        docs.forEach((doc) => {
          doc.ref.delete()
        })
      }).then(() => {
        setLiked(false)
      })
  }

  return (
    <div className="videoSidebar">
      <div className="videoSidebar__buttons">
        {liked ? (
          <Favorite fontSize="large" onClick={onDisLikeVideo} />
        ) : (
            <FavoriteBorder fontSize="large" onClick={onLikeVideo} />
          )
        }
        <p>{likes}</p>
      </div>

      <div className="videoSidebar__buttons" onClick={handleClickOpenMessages}>
        <Message fontSize="large" />
        <p>{messages.length}</p>
      </div>

      <div className="videoSidebar__buttons">
        <Share fontSize="large" />
        <p>{shares}</p>
      </div>

      {/* messagesDialogBox */}
      <Dialog
        open={openMessages}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleCloseMessages}
        aria-labelledby="alert-dialog-slide-title"
        aria-describedby="alert-dialog-slide-description"
        className="videoSidebar__messagesDialogBox"
        maxWidth="sm"
      >
        <DialogTitle id="alert-dialog-slide-title">Messages</DialogTitle>
        <DialogContent>
          {/* <DialogContentText id="alert-dialog-slide-description"> */}
            {messages.map(({ username, message }) => (
              // <>
              //   <Avatar className="videoSidebar__commentedUserAvatar">{username} </Avatar><strong >{username} </strong> { message }<br/>
              // </>
              <List className={classes.root}>
                <ListItem alignItems="flex-start">
                  <ListItemAvatar>
                    <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
                  </ListItemAvatar>
                  <ListItemText
                    primary="Brunch this weekend?"
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          className={classes.inline}
                          color="textPrimary"
                        >
                          Ali Connors
              </Typography>
                        {" — I'll be in your neighborhood doing errands this…"}
                      </React.Fragment>
                    }
                  />
                </ListItem>
                <Divider variant="inset" component="li" />
                <ListItem alignItems="flex-start">
                  <ListItemAvatar>
                    <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
                  </ListItemAvatar>
                  <ListItemText
                    primary="Summer BBQ"
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          className={classes.inline}
                          color="textPrimary"
                        >
                          to Scott, Alex, Jennifer
              </Typography>
                        {" — Wish I could come, but I'm out of town this…"}
                      </React.Fragment>
                    }
                  />
                </ListItem>
                <Divider variant="inset" component="li" />
                <ListItem alignItems="flex-start">
                  <ListItemAvatar>
                    <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg" />
                  </ListItemAvatar>
                  <ListItemText
                    primary="Oui Oui"
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          className={classes.inline}
                          color="textPrimary"
                        >
                          Sandra Adams
              </Typography>
                        {' — Do you have Paris recommendations? Have you ever…'}
                      </React.Fragment>
                    }
                  />
                </ListItem>
              </List>
            ))}
          {/* </DialogContentText> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseMessages} variant="outlined" fontSize="small" color="primary">
            close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  )
}

export default VideoSidebar
