import React, { useEffect, useRef, useState } from 'react'
import { db } from './firebase';
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ videoId, url, channel, description, song, messages, shares, onVideoPress, playing }) {
  const videoRef = useRef(null);
  const [likedUsers, setLikedUsers] = useState(0)
  const [likes, setLikes] = useState(0)

  // pause the video of scrolling
  useEffect(() => {
    if (!playing) {
      videoRef.current.pause()
    }
  }, [playing])

  useEffect(() => {
    async function getLikedUsersSnapshot () {
     const likedUsersSnapshot = await db
                                        .collection("videos")
                                        .doc(videoId)
                                        .collection("likes").get()
      setLikedUsers(likedUsersSnapshot.docs.map(doc => doc.data().userid))
      console.log('data', likedUsers)
    }

    getLikedUsersSnapshot()
      // .then(data => console.log(data))

    // console.log(likedUsersSnapshot)
    // const likedUsersDocs = likedUsersSnapshot.docs.map(doc => doc.data.userid)
    // setLikedUsers(likedUsersSnapshot.docs.map(doc => doc.data.userid))
  }, [videoId, likedUsers])

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        loop
        onClick={onVideoPress}
        ref={videoRef}
      >
      </video>

      <VideoFooter
        channel={channel, likedUsers}
        description={description}
        song={song}
      />

      <VideoSidebar
        likes={likes}
        messages={messages}
        shares={shares}
      />
    </div>
  )
}

export default Video
