import React, { useRef, useState } from 'react'
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ url, channel, description, song, likes, messages, shares, onVideoPress }) {
  // const [playing, setPlaying] = useState(false)
  const videoRef = useRef(null);

  // const onVideoPress = () => {
  //   if (playing) {
  //     videoRef.current.pause();
  //     setPlaying(false)
  //   } else {
  //     videoRef.current.play();
  //     setPlaying(true)
  //   }
  // }

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        loop
        onClick={onVideoPress(videoRef.current)}
        ref={videoRef}
      >
      </video>

      <VideoFooter
        channel={channel}
        description={description}
        song={song}
      />

      <VideoSidebar
        likes={likes}
        messages={messages}
        shares={shares}
      />
    </div>
  )
}

export default Video
