import { Avatar, Dialog, DialogActions, DialogContent, DialogTitle, IconButton, List, ListItem, ListItemAvatar, ListItemText, makeStyles, Slide, TextField, Tooltip, Zoom } from '@material-ui/core';
import { Favorite, FavoriteBorder, HighlightOff, Message, SendRounded, Share } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import { db } from './firebase'
import './VideoSidebar.css'

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

function VideoSidebar({ likes, messages, shares, videoId, userUid, userName }) {
  const [liked, setLiked] = useState(false)
  const [openMessages, setOpenMessages] = React.useState(false);
  const [userMessage, setUserMessage] = React.useState('')

  const useStyles = makeStyles((theme) => ({
    paper: { minWidth: "900px" },
    root: {
      width: '100%',
      maxWidth: '36ch',
      backgroundColor: theme.palette.background.paper,
    },
    inline: {
      display: 'inline',
    },
  }));

  const classes = useStyles()

  const handleClickOpenMessages = () => {
    setOpenMessages(true);
  };

  const handleCloseMessages = () => {
    setOpenMessages(false);
  };

  const handleUserMessageChange = (event) => {
    setUserMessage(event.target.value);
  };

  useEffect(() => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes")
      .onSnapshot((snapshot) => {
        snapshot.docs.forEach((doc) => {
          let docUserId = doc.data().userid
          if (docUserId === userUid) {
            setLiked(true)
          } else {
            setLiked(false)
          }
        })
      })

  }, [videoId, userUid])

  const onLikeVideo = () => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes").add({
        "userid": userUid
      }).then(() => {
        setLiked(true)
      })
  }

  const onDisLikeVideo = () => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes").where('userid', '==', userUid)
      .get()
      .then((docs) => {
        docs.forEach((doc) => {
          doc.ref.delete()
        })
      }).then(() => {
        setLiked(false)
      })
  }

  return (
    <div className="videoSidebar">
      <div className="videoSidebar__buttons">
        {liked ? (
          <Favorite fontSize="large" onClick={onDisLikeVideo} />
        ) : (
            <FavoriteBorder fontSize="large" onClick={onLikeVideo} />
          )
        }
        <p>{likes}</p>
      </div>

      <div className="videoSidebar__buttons" onClick={handleClickOpenMessages}>
        <Message fontSize="large" />
        <p>{messages.length}</p>
      </div>

      <div className="videoSidebar__buttons">
        <Share fontSize="large" />
        <p>{shares}</p>
      </div>

      {/* messagesDialogBox */}
      <Dialog
        open={openMessages}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleCloseMessages}
        aria-labelledby="alert-dialog-slide-title"
        aria-describedby="alert-dialog-slide-description"
        className="videoSidebar__messagesDialogBox"
        maxWidth="sm"
      >
        <DialogTitle id="alert-dialog-slide-title">Messages</DialogTitle>
        <DialogContent>
          <List className={classes.root}>
            {messages.map(({ username, message }) => (
              <ListItem key={username + message} alignItems="flex-start">
                <ListItemAvatar>
                  <Avatar >{username.match(/\b(\w)/g).join('').toUpperCase()}</Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={username}
                  secondary={
                    <React.Fragment>
                      {message}
                    </React.Fragment>
                  }
                />
              </ListItem>
            ))}
          </List>
        </DialogContent>
        <DialogActions id="alert-dialog-slide-title" className="videoSidebar__userMessageBox">
          <Tooltip disabled={userMessage.length > 0 ? false : true} TransitionComponent={Zoom} arrow title="Delete">
            <IconButton onClick={handleCloseMessages} color="primary">
              <HighlightOff disabled={userMessage.length > 0 ? false : true} fontSize="large" />
            </IconButton>
          </Tooltip>
          <TextField
            id="user-message"
            label="add a comment..."
            variant="outlined"
            color="primary"
            multiline
            rowsMax={3}
            value={userMessage}
            onChange={handleUserMessageChange}
          />
          <IconButton disabled={userMessage.length > 0 ? false : true} onClick={handleCloseMessages} color="primary">
            <SendRounded fontSize="large" />
          </IconButton>
        </DialogActions>
      </Dialog>
    </div>
  )
}

export default VideoSidebar
