import { Favorite, FavoriteBorder, Message, Share } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import { db } from './firebase'
import './VideoSidebar.css'

function VideoSidebar({ likes, messages, shares, videoId, userId }) {
  const [liked, setLiked] = useState(false)

  useEffect(() => {
    db.collection("videos")
      .doc(videoId)
      .collection("likes")
      .onSnapshot((snapshot) => {
        snapshot.docs.forEach((doc) => {
          let docUserId = doc.data().userid
          if (docUserId === userId) {
            console.log('if', docUserId, userId)
            setLiked(true)
          } else {
            console.log('else', docUserId, userId)
            setLiked(false)
          }
        })
      })

  }, [videoId, userId])

  const onLikeVideo = () => {
    db.collection("videos")
        .doc(videoId)
        .collection("likes").add({
          "userid": userId
        }).then(() => {
          setLiked(true)
        })
  }

  const onDisLikeVideo = () => {
    db.collection("videos")
        .doc(videoId)
        .collection("likes").where('userid', '==', userId)
        .get()
        .then((docs) => {
          docs.forEach((doc) => {
            doc.ref.delete()
          })
        }).then(() => {
          setLiked(false)
        })
  }

  return (
    <div className="videoSidebar">
      <div className="videoSidebar__buttons">
        {liked ? (
          <Favorite fontSize="large" onClick={onDisLikeVideo} />
        ) : (
            <FavoriteBorder fontSize="large" onClick={onLikeVideo} />
          )
        }
        <p>{likes}</p>
      </div>

      <div className="videoSidebar__buttons">
        <Message fontSize="large" />
        <p>{messages}</p>
      </div>

      <div className="videoSidebar__buttons">
        <Share fontSize="large" />
        <p>{shares}</p>
      </div>
    </div>
  )
}

export default VideoSidebar
