import { useEffect, useRef, useState } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import db from './firebase';
import Video from './Video'
import ScrollTrigger from 'react-scroll-trigger';

function App() {
  const [videos, setVideos] = useState([])
  const scrollRef = useRef(null);

  useEffect(() => {
    db.collection('videos').onSnapshot(snapshot =>
      setVideos(snapshot.docs.map(doc => doc.data()))
    )

    ReactDOM.findDOMNode(scrollRef.current).addEventListener('scroll', onScrollPauseVideo)
  }, [])

  const onScrollPauseVideo = (event) => {
    // event.target.scrollTop;
    console.log('hi', event.target.scrollHeight, event.target.scrollTop)
  }

  const onEnterViewport = () => {
    // this.setState({
    //   visible: true,
    // });
    console.log('hi')
  }

  const onExitViewport =() => {
    // this.setState({
    //   visible: false,
    // });
    console.log('hi')
  }

  return (
    <div className="app">
      <div className="app__videos" ref={scrollRef} onScroll={onScrollPauseVideo}>
        {videos.map(
          (video => (
            <ScrollTrigger onEnter={onEnterViewport} onExit={onExitViewport}>
              <Video
                url={video.url}
                channel={video.channel}
                description={video.description}
                song={video.song}
                likes={video.likes}
                messages={video.messages}
                shares={video.shares}
              />
            </ScrollTrigger>

          ))
        )}
      </div>
    </div>
  );
}

export default App;
