import React, { useEffect, useState } from 'react';
import { db, auth, storage } from './firebase';
import Video from './Video'
import tiktok from './tiktok.jpg'
import './App.css';
import { AccountCircle, Favorite } from '@material-ui/icons';
import { Button, Input, Link, makeStyles, Modal } from '@material-ui/core';

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 300,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function App() {
  const [videos, setVideos] = useState([])
  const [playing, setPlaying] = useState(false)
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [image, setImage] = useState(null)
  const [user, setUser] = useState(null)
  const [userImageUrl, setUserImageUrl] = useState(null)
  const [openSignIn, setOpenSignIn] = useState(false)
  const [disableBackdropClick, setDisableBackdropClick] = useState(false)
  const [disableEscapeKeyDown, setDisableEscapeKeyDown] = useState(false)
  const [openSignUp, setOpenSignUp] = useState(false)

  const classes = useStyles()
  const [modalStyle] = React.useState(getModalStyle)

  useEffect(() => {
    // setUserAvatarMenuOpen(false)
    const unsubscribe = auth.onAuthStateChanged((authUser) => {
      if (authUser) {
        //user has logged in
        setUser(authUser)
      } else {
        //user has logged out
        setUser(null);
      }
    })

    return () => {
      //perform some cleanup functions before refiring the useEffect.
      unsubscribe();
    }
  }, [user])

  useEffect(() => {
    db.collection('videos').onSnapshot(snapshot =>
      setVideos(snapshot.docs.map(doc => ({ id: doc.id, video: doc.data() })))
    )
  }, [])

  const onVideoPress = (videoRef) => {
    if (playing) {
      videoRef.target.pause();
      setPlaying(false)
    } else {
      videoRef.target.play();
      setPlaying(true)
    }
  }

  const onScrollPauseVideo = () => {
    setPlaying(false)
  }

  // signUp
  const signUp = (event) => {
    event.preventDefault();

    auth.createUserWithEmailAndPassword(email, password)
      .then((authUser) => {
        // userData = authUser.user

        //upload user profile photo
        if (image !== null) {
          const uploadTask = storage.ref(`usersphoto/${authUser.user.uid}/${image.name}`).put(image);
          uploadTask.on(
            "state_changed",
            (snapshot) => {
              //progress function
            },
            (error) => {
              console.log(error)
              alert(error.message)
            },
            () => {
              //completes the funciton
              //get url of the uploaded file
              storage
                .ref(`usersphoto/${authUser.user.uid}`)
                .child(image.name)
                .getDownloadURL()
                .then(url => {
                  setUserImageUrl(url)
                })
                .then(
                  alert('sign up completed.')
                )
                .then(
                  // setOpen(false),
                  // setUsername(''),
                  setEmail(''),
                  setPassword(''),
                  setImage(null),
                  // setDisableBackdropClickOnSignInCard(false),
                  // setDisableEscapeKeyDown(false),
                )
            },
          )
        }
        let nameStr = "someone@example.com";
        let nameMatch = nameStr.match(/^([^@]*)@/);
        let username = nameMatch ? nameMatch[1] : null;

        return authUser.user.updateProfile({
          displayName: username,
          photoURL: userImageUrl
        })
      })
      .catch((error) => alert(error.message))
  }

  //signIn
  const signIn = (event) => {
    event.preventDefault();

    setDisableBackdropClick(true)
    setDisableEscapeKeyDown(true)

    auth.signInWithEmailAndPassword(email, password)
      .catch((error) => alert(error.message))
      .then(
        console.log(user, 'logged in successfully'),
        setOpenSignIn(false),
        setEmail(''),
        setPassword(''),
        setDisableBackdropClick(false),
        setDisableEscapeKeyDown(false),
      )
  }

  const openSignUpModal = () => {
    setOpenSignIn(false)
    setOpenSignUp(true)
  }

  return (
    <div className="app">
      <span className="app__copywrite">
        developed with&nbsp;
        <Favorite fontSize="small" className="app__favoriteIcon" />
        <Link target="_blank" href="https://www.facebook.com/hasibahmadkhaliqi">&nbsp;by hak</Link>
      </span>
      <img className="app__backgroundImage" src={tiktok} alt="Noooo" />
      <div className="app__backgroundColor"></div>

      {/* SignIn/up Buttons */}
      <span className="app__signInButton">
        <Button
          color="primary"
          startIcon={<AccountCircle />}
          onClick={() => { setOpenSignIn(true) }}
          size="large"
        >
          sign in
        </Button>
      </span>

      {/* signIn Card */}
      <Modal
        open={openSignIn}
        onClose={() => { setOpenSignIn(false) }}
        disableBackdropClick={disableBackdropClick}
        disableEscapeKeyDown={disableEscapeKeyDown}
      >
        <div style={modalStyle} className={classes.paper}>
          <form className="app__signup">
            <center>
              <img
                className="app__headerImage"
                src="https://logos-world.net/wp-content/uploads/2020/04/TikTok-Emblema.png"
                alt="tik-tok logo"
                width="40%"
              />
            </center>
            <Input
              className="app__signInFeilds"
              placeholder="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Input
              className="app__signInFeilds"
              placeholder="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button className="app__signInFeilds" type="submit" onClick={signIn}>Sign In</Button>
            <Button className="app__signInFeilds" type="submit" onClick={(e) => (e.preventDefault(), auth.signOut())}>Sign Out</Button>
            <Link
              className="app__signUpLink"
              variant="body2"
              onClick={openSignUpModal}
            >
              Don't have an account? Sign up here
            </Link>
          </form>
        </div>
      </Modal>

      {/* signUp Card */}
      <Modal
        open={openSignUp}
        onClose={() => { setOpenSignUp(false) }}
        disableBackdropClick={disableBackdropClick}
        disableEscapeKeyDown={disableEscapeKeyDown}
      >
        <div style={modalStyle} className={classes.paper}>
          <form className="app__signup">
            <center>
              <img
                className="app__headerImage"
                src="https://logos-world.net/wp-content/uploads/2020/04/TikTok-Emblema.png"
                alt="tik-tok logo"
                width="40%"
              />
            </center>
            <Input
              placeholder="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              />
            <Input
              className="app__signInFeilds"
              placeholder="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Input
              className="app__signInFeilds"
              placeholder="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button className="app__signInFeilds" type="submit" onClick={signUp}>Sign Up</Button>
          </form>
        </div>
      </Modal>

      {/* videos */}
      <div className="app__videos" onScroll={onScrollPauseVideo}>
        {videos.map(
          (({ id, video }) => (
            <Video
              key={id}
              url={video.url}
              channel={video.channel}
              description={video.description}
              song={video.song}
              likes={video.likes}
              messages={video.messages}
              shares={video.shares}
              onVideoPress={onVideoPress}
              playing={playing}
            />
          ))
        )}
      </div>
    </div>
  );
}

export default App;
