import React, { useEffect, useRef, useState } from 'react'
import { db } from './firebase';
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ videoId, url, channel, description, song, messages, shares, onVideoPress, playing }) {
  const videoRef = useRef(null);
  const [likes, setLikes] = useState(0)

  // pause the video of scrolling
  useEffect(() => {
    if (!playing) {
      videoRef.current.pause()
    }
  }, [playing])

  useEffect(() => {
    let unsubscribe;

    if (videoId) {
      unsubscribe = db
        .collection("videos")
        .doc(videoId)
        .collection("likes").get()

    }
    return () => {
      unsubscribe()
    }
  }, [videoId])

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        loop
        onClick={onVideoPress}
        ref={videoRef}
      >
      </video>

      <VideoFooter
        channel={channel}
        description={description}
        song={song}
      />

      <VideoSidebar
        likes={likes}
        messages={messages}
        shares={shares}
      />
    </div>
  )
}

export default Video
