import { debounce } from '@material-ui/core';
import React, { useEffect, useRef, useState } from 'react'
import ReactDOM from 'react-dom'
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ url, channel, description, song, likes, messages, shares }) {
  const [playing, setPlaying] = useState(false)
  const videoRef = useRef(null);
  const scrollRef = useRef(null);

  const onVideoPress = () => {
    if (playing) {
      videoRef.current.pause();
      setPlaying(false)
    } else {
      videoRef.current.play();
      setPlaying(true)
    }
  }

  useEffect(() => {
    ReactDOM.findDOMNode(scrollRef.current).addEventListener('scroll', debounce(onScrollPauseVideo, 200))
    return () => {
      ReactDOM.findDOMNode(scrollRef.current).removeEventListener('scroll', onScrollPauseVideo);
    };
  }, [scrollRef.current])

  const onScrollPauseVideo = (event) => {
    // event.target.scrollTop;
    console.log('hi', event)
  }

  return (
    <div className="video" ref={scrollRef} onScroll={onScrollPauseVideo}>
      <video
        className="video__player"
        src={url}
        loop
        onClick={onVideoPress}
        ref={videoRef}
      >
      </video>

      <VideoFooter
        channel={channel}
        description={description}
        song={song}
      />

      <VideoSidebar
        likes={likes}
        messages={messages}
        shares={shares}
      />
    </div>
  )
}

export default Video
