import React, { useEffect, useRef, useState } from 'react'
import './Video.css'
import VideoFooter from './VideoFooter';
import VideoSidebar from './VideoSidebar';

function Video({ videoId, url, channel, description, song, likes, messages, shares, onVideoPress, playing }) {
  const videoRef = useRef(null);

  // pause the video of scrolling
  useEffect(() => {
    if (!playing) {
      videoRef.current.pause()
    }
  }, [playing])

  useEffect(() => {
    let unsubscribe;

    if (postId) {
      unsubscribe = db
        .collection("posts")
        .doc(postId)
        .collection("comments")
        .orderBy('timestamp', 'asc')
        .onSnapshot((snapshot) => {
          setComments(snapshot.docs.map((doc) => {
            return {
              id: doc.id,
              data: doc.data()
            }
          }))
        })
    }
    return () => {
      unsubscribe()
    }
  }, [])

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        loop
        onClick={onVideoPress}
        ref={videoRef}
      >
      </video>

      <VideoFooter
        channel={channel}
        description={description}
        song={song}
      />

      <VideoSidebar
        likes={likes}
        messages={messages}
        shares={shares}
      />
    </div>
  )
}

export default Video
