import { useEffect, useState } from 'react';
import { db, auth, storage } from './firebase';
import Video from './Video'
import tiktok from './tiktok.jpg'
import './App.css';
import { Delete, Favorite } from '@material-ui/icons';
import { Button, Link } from '@material-ui/core';

function App() {
  const [videos, setVideos] = useState([])
  const [playing, setPlaying] = useState(false)
  const [email, setEmail] = useState(null)
  const [password, setPassword] = useState(null)
  const [image, setImage] = useState(null)
  const [userImageUrl, setUserImageUrl] = useState(null);

  useEffect(() => {
    db.collection('videos').onSnapshot(snapshot =>
      setVideos(snapshot.docs.map(doc => doc.data()))
    )
  }, [])

  const onVideoPress = (videoRef) => {
    if (playing) {
      videoRef.target.pause();
      setPlaying(false)
    } else {
      videoRef.target.play();
      setPlaying(true)
    }
  }

  const onScrollPauseVideo = () => {
    setPlaying(false)
  }

  // signUp
  const signUp = (event) => {
    event.preventDefault();

    auth.createUserWithEmailAndPassword(email, password)
      .then((authUser) => {
        // userData = authUser.user

        //upload user profile photo
        if (image !== null) {
          const uploadTask = storage.ref(`usersphoto/${authUser.user.uid}/${image.name}`).put(image);
          uploadTask.on(
            "state_changed",
            (snapshot) => {
              //progress function
            },
            (error) => {
              console.log(error)
              alert(error.message)
            },
            () => {
              //completes the funciton
              //get url of the uploaded file
              storage
                .ref(`usersphoto/${authUser.user.uid}`)
                .child(image.name)
                .getDownloadURL()
                .then(url => {
                  setUserImageUrl(url)
                })
                .then(
                  alert('sign up completed.')
                )
                .then(
                  // setOpen(false),
                  // setUsername(''),
                  setEmail(''),
                  setPassword(''),
                  setImage(null),
                  // setDisableBackdropClick(false),
                  // setDisableEscapeKeyDown(false),
                )
            },
          )
        }
        let nameStr = "someone@example.com";
        let nameMatch = nameStr.match(/^([^@]*)@/);
        let username = nameMatch ? nameMatch[1] : null;

        return authUser.user.updateProfile({
          displayName: username,
          photoURL: userImageUrl
        })
      })
      .catch((error) => alert(error.message))
  }

  return (
    <div className="app">
      <span className="app__copywrite">
        developed with&nbsp;
        <Favorite fontSize="small" className="app__favoriteIcon" />
        <Link target="_blank" href="https://www.facebook.com/hasibahmadkhaliqi">&nbsp;by hak</Link>
      </span>
      <img className="app__backgroundImage" s src={tiktok} alt="Noooo" />
      <div className="app__backgroundColor"></div>

      {/* SignIn/up Buttons */}
      <Button
        color="secondary"
        // className={classes.button}
        startIcon={<Delete />}
      >
        Delete
      </Button>

      {/* videos */}
      <div className="app__videos" onScroll={onScrollPauseVideo}>
        {videos.map(
          (video => (
            <Video
              url={video.url}
              channel={video.channel}
              description={video.description}
              song={video.song}
              likes={video.likes}
              messages={video.messages}
              shares={video.shares}
              onVideoPress={onVideoPress}
              playing={playing}
            />
          ))
        )}
      </div>
    </div>
  );
}

export default App;
