import { useEffect, useRef, useState } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import db from './firebase';
import Video from './Video'

function App() {
  const [videos, setVideos] = useState([])
  const [playing, setPlaying] = useState(false)
  const scrollRef = useRef(null);
  // const videosRef = useRef(null);
  const [videosRefVariable, setVideosRefVariable] = useState(null)



  useEffect(() => {
    db.collection('videos').onSnapshot(snapshot =>
      setVideos(snapshot.docs.map(doc => doc.data()))
    )

    ReactDOM.findDOMNode(scrollRef.current).addEventListener('scroll', onScrollPauseVideo)
  }, [])

  const onVideoPress = (videoRef) => {
    // console.log('calling', videoRef.target.pause())
    setVideosRefVariable(videoRef)
    if (playing) {
      videoRef.target.pause();
      setPlaying(false)
    } else {
      videoRef.target.play();
      setPlaying(true)
    }
  }

  const onScrollPauseVideo = (event) => {
    setPlaying(false)
    // onVideoPress(videosRefVariable)
  }

  return (
    <div className="app">
      <div className="app__videos" ref={scrollRef} onScroll={onScrollPauseVideo}>
        {videos.map(
          (video => (
            <Video
              url={video.url}
              channel={video.channel}
              description={video.description}
              song={video.song}
              likes={video.likes}
              messages={video.messages}
              shares={video.shares}
              onVideoPress={onVideoPress}
            />
          ))
        )}
      </div>
    </div>
  );
}

export default App;
